/*!40000 ALTER TABLE `meo` DISABLE KEYS */;
INSERT INTO `meo` VALUES (1,'6 skills trong căn bếp bữa ăn ngon','Chỉ cần nắm vững những kỹ năng này, trình nấu nướng của bạn sẽ được nâng cấp, dù thực tế bạn không quá thích vào bếp hay nấu ăn giỏi.','/image/6skillslambep/bokho.png','/MeoVat/6-Skills-lam-bep','6-Skills-lam-bep'),(2,'6 bí kíp chọn thực phẩm tươi ngon','Với 10 mẹo nhận biết dưới đây, bạn dễ dàng loại bỏ những loại thực phẩm không an toàn. Đảm bào sức khỏe cho bản thân và gia đình.','/image/10bikipchonthucphamtuoi/trungga.jpg','/MeoVat/10-bi-kip-chon-thuc-pham-tuoi','10-bi-kip-chon-thuc-pham-tuoi'),(3,'Cách làm sạch ấm đun nước điện','Tần suất làm sạch sẽ phụ thuộc vào tần suất sử dụng ấm nước của bạn.Bên ngoài nên được lau loại bỏ các vết ố và bẩn tung tóe ít nhất một lần một tuần','/image/lamsachamdunnuoc/1.jpg','/MeoVat/Cach-lam-sang-am-dun-nuoc-dien','Cach-lam-sang-am-dun-nuoc-dien'),(4,'6 mẹo nấu ăn hữu ích bạn có thể học lỏm từ đầu bếp nhà hàng','Cắt lát chanh càng mỏng, bạn sẽ cắt được lát chanh càng chua','/image/9meocooking/meo3-6308-1506331373.jpg','/MeoVat/9-meo-nau-an-huu-ich','6-meo-don-gian'),(5,'6 lưu ý nấu ăn đơn giản','Cho người đầu bếp mới bắt đầu, 6 sai lầm nấu ăn phổ biến mà mọi người thường mắc phải và cách mà bạn có thể tránh chúng','https://hotelcareers.vn/wp-content/uploads/2022/07/meo-vat-nau-an.jpg','/MeoVat/6-luu-y-cho-nguoi-moi-bat-dau','6-luu-y-cho-nguoi-moi-bat-dau'),(6,'Hướng dẫn nấu ăn cùng các loại ngũ cốc','Nếu bạn muốn bổ sung thêm nhiều ngũ cốc nguyên cám vào chế độ ăn uống thì bạn có thể tìm hiểu hướng dẫn nấu ăn với ngũ cốc đơn giản này','https://cdn.tgdd.vn/2020/12/CookProduct/thumb1-1200x676-11.jpg','/MeoVat/Nau-an-voi-ngu-coc','Nau-an-voi-ngu-coc'),(7,'4 mẹo biến thịt dai thành mềm mại','Những thủ thuật này được các đầu bếp có thâm niên đúc kết ra, có thể chữa thịt dai trở nên mềm mại, ngon tuyệt','https://s1.media.ngoisao.vn/news/2023/04/06/heo-dai-3-ngoisaovn-w1200-h675.jpg','/MeoVat/4-meo-bien-thit-dai-thanh-thit-mem','4-meo-bien-thit-dai-thanh-thit-mem'),(8,'Công thức nấu ăn và cháo ngon của đầu bếp Singapore','Cho gạo đã vo sạch để qua đêm sẽ giúp nấu cơm hoặc cháo đỡ dính và nhanh hơn','https://img-global.cpcdn.com/recipes/466d4da831efb91b/1200x630cq70/photo.jpg','/MeoVat/chao-ech-singapore','chao-ech-singapore'),(9,'Mẹo khử mùi tanh của cá','Mùi tanh là một trong những vấn đề cực kì phổ biến trong các món ăn có chứa cá, hãy cùng 14cooking giải quyết vấn đề này','https://media-cdn-v2.laodong.vn/Storage/NewsPortal/2021/5/13/909026/186107386_1937994386.jpg','/MeoVat/meo-khu-mui-tanh-cua-ca','meo-khu-mui-tanh-cua-ca'),(10,'5 công thức sáng tạo với máy xay cầm tay','Dưới đây là một vài công thức tuyệt vời để bạn làm với máy xay cầm tay và những phụ kiện tiện dụng của nó!','https://media3.bosch-home.com/Images/600x/MCIM02008190_FR09-KL-R1-5WaysHandBlender-Hollandaise.webp','/MeoVat/5-creative-recipes-with-a-hand-blender','5-creative-recipes-with-a-hand-blender'),(11,'6 bí quyết để loại bỏ các tác nhân gây dị ứng tại nhà của bạn','Những tác nhân gây dị ứng này có thể ít ảnh hưởng đến người khác, nhưng lại khiến bạn khò khè và ngứa ngáy toàn thân.','https://media3.bosch-home.com/Images/1600x/MCIM02007500_FR03-SC-B2-7HacksAllergy-Sunshine.webp','/MeoVat/6-hacks-to-allergy-proof-your-home','6-hacks-to-allergy-proof-your-home'),(12,'6 cách bếp từ Bosch nâng cao trải nghiệm nấu nướng của bạn','6 cách bếp từ Bosch nâng cao trải nghiệm nấu nướng của bạn','https://media3.bosch-home.com/Images/1600x/12492931_RAP_FR_InductionHobEnhanceHead_master.webp','/MeoVat/6-ways-bosch-induction','6-ways-bosch-induction'),(13,'6 bí quyết đơn giản giữ cho tủ lạnh gọn gàng, ngăn nắp','Bạn có biết rằng việc giữ cho tủ lạnh gọn gàng, ngăn nắp là cách đơn giản giúp tiết kiệm thời gian và tiền bạc cho những lần đi siêu thị?','https://media3.bosch-home.com/Images/1600x/MCIM02007223_FR33-SC-7SimpleHacksFridges.webp','/MeoVat/keep-your-fridge-fresh-and-organised','keep-your-fridge-fresh-and-organised'),(14,'6 loại thực phẩm không nên giữ trong tủ lạnh','Tủ lạnh có thể kéo dài thời gian sử dụng của thực phẩm nhưng cũng có thể gây hại cho chúng.','https://media3.bosch-home.com/Images/1600x/MCIM02007216_FR26-KLSC-10ThingsNeverKeepInFridge.webp','/MeoVat/6-types-of-food-you-should-never-keep-in-the-fridge','6-types-of-food-you-should-never-keep-in-the-fridge'),(15,'Làm chủ các phương pháp nấu ăn nhiệt ẩm','Hãy khám phá và làm chủ 5 phương pháp nấu ăn nhiệt ẩm dưới đây để có những bữa ăn ngon miệng và đầy dinh dưỡng cho gia đình bạn.','https://media3.bosch-home.com/Images/1600x/MCIM02007220_FR30-KL-MasterMoistHeatCooking.webp','/MeoVat/master-moist-heat-cooking-methods','master-moist-heat-cooking-methods'),(16,'Làm thế nào để nấu thịt đúng cách?','Mỗi loại thịt đều có những thách thức của riêng chúng và có thể quyết định một món ăn ngon hay dở.','https://media3.bosch-home.com/Images/1600x/MCIM02007202_FR12-KL-CookingMeatRight.webp','/MeoVat/how-to-cook-meat-right','how-to-cook-meat-right'),(17,'5 ý tưởng lưu trữ thông minh và hiệu quả cho căn bếp nhà bạn','Dưới đây là 5 mẹo hàng đầu của chúng tôi để giúp bạn bắt đầu sắp xếp gian bếp của mình.','https://media3.bosch-home.com/Images/1600x/MCIM02007209_FR19-SC-5EffecientStorage.webp','/MeoVat/5-smart-efficient-storage-ideas-for-your-kitchen','5-smart-efficient-storage-ideas-for-your-kitchen'),(18,'7 mẹo nấu ăn dễ dàng cho những người lười biếng','Chỉ riêng suy nghĩ về việc phải lên kế hoạch, chuẩn bị nguyên liệu và dọn dẹp sau khi nấu đã quá đủ để khiến nhiều người bỏ đi ý tưởng tự nấu ăn.','https://media3.bosch-home.com/Images/1600x/MCIM02240086_FR42-KL-Stage-7CookingTipsLazyPeople.webp','/MeoVat/7-easy-recipes-for-lazy-people','7-easy-recipes-for-lazy-people');
/*!40000 ALTER TABLE `meo` ENABLE KEYS */;
